import React, { Component } from 'react'

export class Hashing extends Component {

  constructor(props)
  {
    super(props);
    this.state =
    {
      hashedString: '',
      isReady: false,
      isComplete: false
    };
  }

  requestHash()
  {
    this.setState({...this.state, hashedString: 'API result'}, () =>
    {
      this.toggleReadiness();
    });
  }

  requestToUnhash()
  {
    this.setState({...this.state, unhashedString: 'unhashed', isComplete: true}, () =>
    {
      this.toggleReadiness();
    });
  }

  toggleReadiness()
  {
    this.setState({...this.state, isReady: !this.state?.isReady});
  }

  render() {
    return (
      <>
        {
          (this.state?.isReady)
          ?
            <>
              <p>the hashed string is: {this.state?.hashedString}</p>
              <button onClick={() => this.requestToUnhash()}>
                <p>unhash here</p>
              </button>
            </>
          :
            <>
              {
                (this.state?.isComplete)
                ?
                  <p>
                    {
                      this.state?.unhashedString
                    }
                  </p>
                :
                  null
              }
              <button onClick={() => this.requestHash()}>
                <p>request a {this.state?.isComplete ? 'new ' : null}hash from API</p>
              </button>
            </>
        }      
      </>    
    )
  }
}
