import React, { Component } from 'react';
import axios from 'axios';
import CryptoJS from 'crypto-js';

export class Hashing extends Component
{
	constructor(props)
	{
		super(props);
		this.state =
		{
			usersData: [],
			key: '',
			isRequested: false
		};
	}

	requestHash()
	{
		this.setState({...this.state, isRequested: true}, () =>
		{
			try
			{
				axios?.get('http://35.180.138.146:3000/v1/user/account/generateKey',
				{
					headers:
					{
						'Access-Control-Allow-Origin': '*',
						'x-api-version': '1',
						'x-api-lang': 'en',
						'x-api-key': '6712af02b880c31fc7ede1f6774f31d1ce0b02aa9a3rt',
						'x-api-endpoint': 'web'
					}
				})
				?.then(res =>
				{
					if(res?.data?.is_successful)
					{
						this.setState({...this.state, key: res?.data?.key}, () =>
						{
							axios?.get('http://35.180.138.146:3000/v1/user/account/getUsersInfo',
							{
								headers:
								{
									'Access-Control-Allow-Origin': '*',
									'x-api-version': '1',
									'x-api-lang': 'en',
									'x-api-key': '6712af02b880c31fc7ede1f6774f31d1ce0b02aa9a3rt',
									'x-api-endpoint': 'web'
								}
							})
							?.then(res =>
							{
								if(res?.data?.is_successful)
								{
									var usersData = this.state?.usersData;
									usersData?.forEach(user =>
									{

										user['email'] = CryptoJS.AES.decrypt("U2FsdGVkX1/n342PU4WHazNNvZhHQZdsEb2S5OhJSWI=", this.state?.key);
										user['mobile'] = CryptoJS.AES.decrypt("U2FsdGVkX1/n342PU4WHazNNvZhHQZdsEb2S5OhJSWI=", this.state?.key);
										user['username'] = CryptoJS.AES.decrypt("U2FsdGVkX1/n342PU4WHazNNvZhHQZdsEb2S5OhJSWI=", this.state?.key);
									})
									var decrypted = CryptoJS.AES.decrypt("U2FsdGVkX1/n342PU4WHazNNvZhHQZdsEb2S5OhJSWI=", this.state?.key);
									var originalText = decrypted.toString(CryptoJS.enc.Utf8);
									console.log(originalText);
									this.setState({...this.state, usersData:res?.data?.response});
									console.log(res?.data?.response);
								}
							})
							.catch(e =>
							{
								console.error(e);
							});
						});
					}
				})
				.catch(e =>
				{
					console.error(e);
				});
			}
			catch (e)
			{
				console.error(e);
			}
		});

	}

	render()
	{
		return (
			<>
				<div className='mainContainer'>
					{
						(this.state?.isRequested)
						?
							<>
								{
									this.state?.users?.map((user, index) =>
									{
										return (<p key={index}>{user?.username}</p>)

									})
								}
							</>
						:
							<>
								<button onClick={() => this.requestHash()}>Request a Hash</button>
							</>
					}
				</div>
			</>
		);
	}
}
