import React, { Component } from 'react'
import axios from 'axios';

export class Hashing extends Component {

  constructor(props) {
    super(props);
    this.state =
    {
      hashedString: '',
      isReady: false,
      isComplete: false
    };
  }

  requestHash() {
    try {
      axios({
        method: 'get',
        url: 'http://35.180.138.146:3000/v1/user/account/generateKey',
      })
        .then(res => {

          this.setState({ ...this.state, hashedString: 'API result' }, () => {
            this.toggleReadiness();
          });
        });
    }
    catch (error) {
      alert(error)
    }
  }

  requestToUnhash() {
    this.setState({ ...this.state, unhashedString: 'unhashed', isComplete: true }, () => {
      this.toggleReadiness();
    });
  }

  toggleReadiness() {
    this.setState({ ...this.state, isReady: !this.state?.isReady });
  }

  render() {
    return (
      <>

      </>
    )
  }
}
