import { useState } from 'react';
export function Hashing()
{
  const [isReady, setIsReady] = useState(0);
  const [hashed, setHashed] = useState('');
  const [unhashed, setUnhashed] = useState('unhashed');

  const requestHash = () =>
  {
      setHashed(_requestHash());
      toggleIsReady();
  }

  const toggleIsReady = () => {
    alert(isReady + ' - 1')
    setIsReady(isReady === 1 ? 0 : 1);    
    alert(isReady + ' - 2')
  }

  return (
    <>
      {
        ({isReady} === 1)
        ?
          <>
            <p>The hashed statement is: {hashed}</p>
            <p>The unhashed statement is: {unhashed}</p>
          </>
        :
          <>
            <div>
              <button onClick={() => requestHash()}>
                Request a hash
              </button>

              <button onClick={() => requestHash()}>
                Request to unhash
              </button>
            </div>
          </>
      }
    </>
  );
}

function _requestHash()
{
  return '123';
}
